use std::env;
use std::process::Command;

fn main() {
    println!("cargo:rerun-if-changed=build.rs");
    println!("cargo::rustc-check-cfg=cfg(gpu_available)");

    // Detect GPU capabilities and automatically enable features
    let detected_features = detect_gpu_capabilities();
    let has_gpu = !detected_features.is_empty();

    // If we detected GPU capabilities, enable them by default
    for feature in detected_features {
        println!("cargo:rustc-cfg=feature=\"{feature}\"");
        eprintln!("ğŸ”§ Build: Auto-detected GPU feature: {feature}");
    }

    // Set default GPU mode based on detection
    if has_gpu {
        println!("cargo:rustc-cfg=gpu_available");
        eprintln!("ğŸš€ Build: GPU acceleration will be enabled by default");
    } else {
        eprintln!("ğŸ’» Build: No GPU acceleration detected - CPU mode will be used");
    }
}

fn detect_gpu_capabilities() -> Vec<String> {
    let mut features = Vec::new();

    // Detect Apple Silicon (Metal)
    if is_apple_silicon() {
        features.push("gpu-metal".to_string());
        eprintln!("ğŸ Build: Apple Silicon detected - enabling Metal GPU support");
    }

    // Detect NVIDIA GPU (CUDA)
    if has_nvidia_gpu() {
        features.push("gpu-cuda".to_string());
        eprintln!("ğŸŸ¢ Build: NVIDIA GPU detected - enabling CUDA support");
    }

    // Detect Intel MKL
    if has_intel_mkl() {
        features.push("gpu-mkl".to_string());
        eprintln!("ğŸ”µ Build: Intel MKL detected - enabling MKL support");
    }

    features
}

fn is_apple_silicon() -> bool {
    #[cfg(target_os = "macos")]
    {
        // Check if running on Apple Silicon
        match Command::new("uname").arg("-m").output() {
            Ok(output) => {
                let arch = String::from_utf8_lossy(&output.stdout);
                arch.trim() == "arm64"
            }
            Err(_) => false,
        }
    }
    #[cfg(not(target_os = "macos"))]
    {
        false
    }
}

fn has_nvidia_gpu() -> bool {
    // Try to detect NVIDIA GPU
    #[cfg(target_os = "linux")]
    {
        // Check for nvidia-smi
        if Command::new("nvidia-smi").output().is_ok() {
            return true;
        }

        // Check for CUDA runtime
        if Command::new("nvcc").arg("--version").output().is_ok() {
            return true;
        }
    }

    #[cfg(target_os = "windows")]
    {
        // Check for NVIDIA driver
        if Command::new("nvidia-smi.exe").output().is_ok() {
            return true;
        }
    }

    // Check environment variables
    env::var("CUDA_PATH").is_ok() || env::var("CUDA_HOME").is_ok()
}

fn has_intel_mkl() -> bool {
    // Check for Intel MKL
    env::var("MKLROOT").is_ok()
        || env::var("MKL_ROOT").is_ok()
        || Command::new("pkg-config")
            .args(["--exists", "mkl"])
            .output()
            .is_ok_and(|o| o.status.success())
}

use clap::Parser;

/// Check if GPU acceleration is available at compile time
fn is_gpu_available() -> bool {
    #[cfg(gpu_available)]
    {
        true
    }
    #[cfg(not(gpu_available))]
    {
        false
    }
}

#[derive(Parser, Clone, Debug, Resource)]
#[command(
    name = "ai-code-buddy",
    version = "0.2.0",
    about = "ğŸ¤– AI-powered code review tool with elegant TUI",
    long_about = "AI Code Buddy is an intelligent code analysis tool that compares branches, \
                  detects security vulnerabilities, performance issues, and code quality problems. \
                  Features a modern Bevy-powered TUI with real-time analysis and reporting."
)]
pub struct Args {
    /// Git repository path to analyze
    #[arg(
        value_name = "REPO_PATH",
        default_value = ".",
        help = "Path to the Git repository (default: current directory)"
    )]
    pub repo_path: String,

    /// Source branch for comparison
    #[arg(
        short = 's',
        long = "source",
        value_name = "BRANCH",
        default_value = "main",
        help = "Source branch to compare from"
    )]
    pub source_branch: String,

    /// Target branch for comparison
    #[arg(
        short = 't',
        long = "target",
        value_name = "BRANCH",
        default_value = "HEAD",
        help = "Target branch to compare to (default: HEAD)"
    )]
    pub target_branch: String,

    /// Use CLI mode instead of interactive TUI
    #[arg(
        long = "cli",
        help = "Run in CLI mode with text output instead of interactive interface"
    )]
    pub cli_mode: bool,

    /// Enable verbose output
    #[arg(
        short = 'v',
        long = "verbose",
        help = "Enable verbose output for debugging"
    )]
    pub verbose: bool,

    /// Show credits and contributors
    #[arg(
        long = "credits",
        help = "Show credits and list all contributors to the project"
    )]
    pub show_credits: bool,

    /// Output format for results
    #[arg(
        short = 'f',
        long = "format",
        value_enum,
        default_value = "summary",
        help = "Output format for results"
    )]
    pub output_format: OutputFormat,

    /// Exclude files matching pattern
    #[arg(
        long = "exclude",
        value_name = "PATTERN",
        help = "Exclude files matching glob pattern (can be used multiple times)",
        action = clap::ArgAction::Append
    )]
    pub exclude_patterns: Vec<String>,

    /// Include only files matching pattern
    #[arg(
        long = "include",
        value_name = "PATTERN",
        help = "Include only files matching glob pattern (can be used multiple times)",
        action = clap::ArgAction::Append
    )]
    pub include_patterns: Vec<String>,

    /// Enable GPU acceleration for AI analysis
    #[arg(
        long = "gpu",
        help = "Enable GPU acceleration (Metal on Apple, CUDA on NVIDIA, auto-detected)",
        default_value_t = is_gpu_available()
    )]
    pub use_gpu: bool,

    /// Force CPU mode (disable GPU acceleration)
    #[arg(
        long = "cpu",
        help = "Force CPU mode (disable GPU acceleration even if available)",
        conflicts_with = "use_gpu"
    )]
    pub force_cpu: bool,
}

#[derive(clap::ValueEnum, Clone, Debug, PartialEq)]
pub enum OutputFormat {
    /// Summary output with key findings
    Summary,
    /// Detailed output with all issues
    Detailed,
    /// JSON format for programmatic use
    Json,
    /// Markdown format for documentation
    Markdown,
}

use bevy::prelude::Resource;

use bevy::prelude::*;

#[derive(Debug, Copy, Clone, Eq, PartialEq, Hash, Default, States)]
pub enum AppState {
    #[default]
    Overview,
    Analysis,
    Reports,
}

use anyhow::Result;
use serde::{Deserialize, Serialize};
use std::path::Path;
use tokio::sync::mpsc;

use crate::core::review::{CommitStatus, Issue};

#[derive(Debug, Clone, Serialize, Deserialize)]
pub struct AnalysisRequest {
    pub file_path: String,
    pub content: String,
    pub language: String,
    pub commit_status: CommitStatus,
}

#[derive(Debug, Clone)]
pub struct ProgressUpdate {
    pub current_file: String,
    pub progress: f64,
    pub stage: String,
}

#[derive(Debug, Clone, PartialEq)]
pub enum GpuBackend {
    Metal,
    Cuda,
    Mkl,
    Cpu,
}

impl std::fmt::Display for GpuBackend {
    fn fmt(&self, f: &mut std::fmt::Formatter<'_>) -> std::fmt::Result {
        match self {
            GpuBackend::Metal => write!(f, "Metal"),
            GpuBackend::Cuda => write!(f, "CUDA"),
            GpuBackend::Mkl => write!(f, "MKL"),
            GpuBackend::Cpu => write!(f, "CPU"),
        }
    }
}

pub struct AIAnalyzer {
    backend: GpuBackend,
}

impl AIAnalyzer {
    pub async fn new(use_gpu: bool) -> Result<Self> {
        println!("ğŸ§  Initializing AI analyzer...");

        // Detect and configure GPU backend
        let backend = if use_gpu {
            Self::detect_gpu_backend()
        } else {
            GpuBackend::Cpu
        };

        println!("ğŸ”§ Using backend: {backend:?}");

        println!("ğŸ” AI inference currently disabled due to token sampling issues");
        println!("ğŸ”§ Using enhanced rule-based analysis for comprehensive code review");

        let analyzer = AIAnalyzer { backend };

        // Display the configured backend for diagnostics
        println!(
            "ğŸ”§ AI Analyzer initialized with {} backend",
            analyzer.get_backend()
        );

        Ok(analyzer)
    }

    /// Get the GPU backend being used by this analyzer
    pub fn get_backend(&self) -> &GpuBackend {
        &self.backend
    }

    fn detect_gpu_backend() -> GpuBackend {
        // Check if we're on Apple Silicon (Metal support)
        if cfg!(target_os = "macos") && Self::is_apple_silicon() {
            println!("ğŸ Apple Silicon detected, using Metal backend");
            GpuBackend::Metal
        }
        // Check for CUDA support (NVIDIA)
        else if Self::has_cuda_support() {
            println!("ğŸŸ¢ NVIDIA CUDA detected, using CUDA backend");
            GpuBackend::Cuda
        }
        // Check for Intel MKL support
        else if Self::has_mkl_support() {
            println!("ğŸ”µ Intel MKL detected, using MKL backend");
            GpuBackend::Mkl
        }
        // Fallback to CPU
        else {
            println!("ğŸ’» No GPU acceleration detected, falling back to CPU");
            GpuBackend::Cpu
        }
    }

    fn is_apple_silicon() -> bool {
        // Check if we're running on Apple Silicon
        cfg!(target_arch = "aarch64") && cfg!(target_os = "macos")
    }

    fn has_cuda_support() -> bool {
        // Check for NVIDIA GPU presence
        // This is a simplified check - in production you might want to check for actual CUDA runtime
        std::process::Command::new("nvidia-smi")
            .output()
            .map(|output| output.status.success())
            .unwrap_or(false)
    }

    fn has_mkl_support() -> bool {
        // Check for Intel processor
        // This is a simplified check
        cfg!(target_arch = "x86_64")
    }

    pub async fn analyze_file(
        &self,
        request: AnalysisRequest,
        progress_tx: Option<mpsc::UnboundedSender<ProgressUpdate>>,
    ) -> Result<Vec<Issue>> {
        let _language = self.detect_language(&request.file_path);

        if let Some(ref tx) = progress_tx {
            let _ = tx.send(ProgressUpdate {
                current_file: request.file_path.clone(),
                progress: 0.0,
                stage: "Starting analysis".to_string(),
            });
        }

        let mut issues = Vec::new();

        // AI inference is currently disabled due to token sampling issues
        // Using enhanced rule-based analysis which provides comprehensive coverage
        issues.extend(self.rule_based_analysis(&request)?);

        // TODO: Re-enable AI analysis once token sampling issues are resolved
        // The AI methods are preserved below for future use

        if let Some(ref tx) = progress_tx {
            let _ = tx.send(ProgressUpdate {
                current_file: request.file_path.clone(),
                progress: 100.0,
                stage: "Analysis complete".to_string(),
            });
        }

        Ok(issues)
    }

    fn rule_based_analysis(&self, request: &AnalysisRequest) -> Result<Vec<Issue>> {
        let mut issues = Vec::new();

        for (line_num, line) in request.content.lines().enumerate() {
            let line_number = line_num + 1;
            let line_lower = line.to_lowercase();

            // SECURITY PATTERNS

            // Hardcoded credentials
            if (line_lower.contains("password")
                || line_lower.contains("api_key")
                || line_lower.contains("secret"))
                && line.contains("=")
                && (line.contains("\"") || line.contains("'"))
            {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "Critical".to_string(),
                    category: "Security".to_string(),
                    description: "Hardcoded credentials detected - use environment variables"
                        .to_string(),
                    commit_status: request.commit_status.clone(),
                });
            }

            // Code injection
            if line.contains("eval(") || line.contains("exec(") {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "Critical".to_string(),
                    category: "Security".to_string(),
                    description: "Code injection vulnerability - avoid eval/exec".to_string(),
                    commit_status: request.commit_status.clone(),
                });
            }

            // SQL injection patterns
            if line.contains("query")
                && line.contains("format!")
                && (line.contains("SELECT") || line.contains("INSERT") || line.contains("UPDATE"))
            {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "Critical".to_string(),
                    category: "Security".to_string(),
                    description: "Potential SQL injection - use parameterized queries".to_string(),
                    commit_status: request.commit_status.clone(),
                });
            }

            // Command injection patterns
            if (line.contains("Command::new")
                || line.contains("subprocess")
                || line.contains("system("))
                && (line.contains("format!")
                    || line.contains("user_input")
                    || line.contains("args"))
            {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "Critical".to_string(),
                    category: "Security".to_string(),
                    description: "Command injection vulnerability - sanitize inputs".to_string(),
                    commit_status: request.commit_status.clone(),
                });
            }

            // Path traversal patterns
            if line.contains("../")
                && (line.contains("read") || line.contains("open") || line.contains("file"))
            {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "High".to_string(),
                    category: "Security".to_string(),
                    description: "Path traversal vulnerability - validate file paths".to_string(),
                    commit_status: request.commit_status.clone(),
                });
            }

            // PERFORMANCE PATTERNS

            // Nested loops (O(nÂ²) complexity)
            if line.contains("for") && line.trim().starts_with("for") {
                // Check if there's another for loop nearby (simple heuristic)
                let lines: Vec<&str> = request.content.lines().collect();
                for (idx, _) in lines
                    .iter()
                    .enumerate()
                    .take(std::cmp::min(line_num + 10, lines.len()))
                    .skip(line_num + 1)
                {
                    if lines[idx].trim().starts_with("for") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Medium".to_string(),
                            category: "Performance".to_string(),
                            description: "Nested loops detected - consider optimization"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                        break;
                    }
                }
            }

            // Language-specific analysis
            match request.language.as_str() {
                "rust" => {
                    // Security
                    if line.contains("unsafe") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "High".to_string(),
                            category: "Security".to_string(),
                            description: "Unsafe code block - requires justification and review"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }

                    if line.contains("std::ptr::null") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Critical".to_string(),
                            category: "Security".to_string(),
                            description: "Null pointer dereference - will cause segfault"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }

                    // Error handling
                    if line.contains("unwrap()") && !line.contains("expect(") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Medium".to_string(),
                            category: "Error Handling".to_string(),
                            description:
                                "Use expect() or proper error handling instead of unwrap()"
                                    .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }

                    // Performance
                    if line.contains(".clone()") && line.contains("&") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Low".to_string(),
                            category: "Performance".to_string(),
                            description: "Unnecessary clone - consider borrowing instead"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }
                }
                "python" => {
                    // Security
                    if line.contains("pickle.loads") && !line.contains("trusted") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Critical".to_string(),
                            category: "Security".to_string(),
                            description: "Unsafe deserialization - pickle.loads is dangerous"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }

                    if line.contains("yaml.load") && !line.contains("safe_load") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "High".to_string(),
                            category: "Security".to_string(),
                            description: "Use yaml.safe_load instead of yaml.load".to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }

                    // Performance
                    if line.contains("+=") && (line.contains("\"") || line.contains("'")) {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Medium".to_string(),
                            category: "Performance".to_string(),
                            description:
                                "String concatenation in loop - use join() for better performance"
                                    .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }
                }
                "javascript" | "typescript" => {
                    // Security
                    if line.contains("innerHTML") && line.contains("+") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "High".to_string(),
                            category: "Security".to_string(),
                            description: "XSS vulnerability - validate before setting innerHTML"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }

                    // Performance
                    if line.contains("document.getElementById") && line.contains("for") {
                        issues.push(Issue {
                            file: request.file_path.clone(),
                            line: line_number,
                            severity: "Medium".to_string(),
                            category: "Performance".to_string(),
                            description: "DOM query in loop - cache the element reference"
                                .to_string(),
                            commit_status: request.commit_status.clone(),
                        });
                    }
                }
                _ => {}
            }

            // CODE QUALITY PATTERNS

            if line.contains("TODO") || line.contains("FIXME") || line.contains("HACK") {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "Low".to_string(),
                    category: "Code Quality".to_string(),
                    description: "Code comment indicates incomplete implementation".to_string(),
                    commit_status: request.commit_status.clone(),
                });
            }

            // Long line detection
            if line.len() > 120 {
                issues.push(Issue {
                    file: request.file_path.clone(),
                    line: line_number,
                    severity: "Low".to_string(),
                    category: "Code Quality".to_string(),
                    description: format!(
                        "Line too long ({} chars) - consider breaking into multiple lines",
                        line.len()
                    ),
                    commit_status: request.commit_status.clone(),
                });
            }
        }

        Ok(issues)
    }

    fn detect_language(&self, file_path: &str) -> String {
        let path = Path::new(file_path);
        match path.extension().and_then(|ext| ext.to_str()) {
            Some("rs") => "rust".to_string(),
            Some("js") => "javascript".to_string(),
            Some("ts") => "typescript".to_string(),
            Some("py") => "python".to_string(),
            Some("java") => "java".to_string(),
            Some("cpp") | Some("cc") | Some("cxx") => "cpp".to_string(),
            Some("c") => "c".to_string(),
            Some("go") => "go".to_string(),
            Some("php") => "php".to_string(),
            Some("rb") => "ruby".to_string(),
            Some("cs") => "csharp".to_string(),
            _ => "unknown".to_string(),
        }
    }
}

use crate::args::Args;
use crate::core::{
    ai_analyzer::{AIAnalyzer, AnalysisRequest, ProgressUpdate},
    git::GitAnalyzer,
    review::Review,
};
use anyhow::Result;
use tokio::sync::mpsc;

pub async fn perform_analysis_with_progress(
    args: &Args,
    progress_callback: Option<Box<dyn Fn(f64, String) + Send + Sync>>,
) -> Result<Review> {
    println!("ğŸ“Š Starting AI-powered analysis...");

    let git_analyzer = GitAnalyzer::new(&args.repo_path)?;

    // Get changed files between branches
    let changed_files = git_analyzer.get_changed_files(&args.source_branch, &args.target_branch)?;

    println!("ğŸ“ˆ Found {} changed files", changed_files.len());

    let mut review = Review {
        files_count: changed_files.len(),
        issues_count: 0,
        critical_issues: 0,
        high_issues: 0,
        medium_issues: 0,
        low_issues: 0,
        issues: Vec::new(),
    };

    // Initialize AI analyzer
    let use_gpu = args.use_gpu && !args.force_cpu;
    if args.force_cpu {
        println!("ğŸ’» CPU mode forced by user with --cpu flag");
    } else if args.use_gpu {
        println!("ğŸš€ GPU acceleration enabled (auto-detected or requested)");
    }
    let ai_analyzer = AIAnalyzer::new(use_gpu).await?;

    // Create progress channel
    let (progress_tx, mut progress_rx) = mpsc::unbounded_channel::<ProgressUpdate>();

    // Spawn task to handle progress updates
    if let Some(callback) = progress_callback {
        tokio::spawn(async move {
            while let Some(update) = progress_rx.recv().await {
                // Format the current file with stage information
                let status_message = if update.stage.is_empty() {
                    update.current_file
                } else {
                    format!("{} - {}", update.current_file, update.stage)
                };
                callback(update.progress, status_message);
            }
        });
    }

    // Analyze each file
    let total_files = changed_files.len() as f64;
    for (index, file_path) in changed_files.iter().enumerate() {
        if should_analyze_file(file_path, args) {
            let commit_status = git_analyzer
                .get_file_status(file_path)
                .unwrap_or(crate::core::review::CommitStatus::Committed);

            let status_indicator = match commit_status {
                crate::core::review::CommitStatus::Committed => "ğŸ“„",
                crate::core::review::CommitStatus::Staged => "ğŸ“‘",
                crate::core::review::CommitStatus::Modified => "ğŸ“",
                crate::core::review::CommitStatus::Untracked => "ğŸ“„",
            };

            let file_progress = (index as f64 / total_files) * 100.0;
            println!(
                "  {status_indicator} Analyzing: {file_path} ({commit_status:?}) [{file_progress:.1}%]"
            );

            if let Ok(content) = git_analyzer.get_file_content(file_path, &args.target_branch) {
                let request = AnalysisRequest {
                    file_path: file_path.clone(),
                    content,
                    language: detect_language(file_path),
                    commit_status,
                };

                match ai_analyzer
                    .analyze_file(request, Some(progress_tx.clone()))
                    .await
                {
                    Ok(file_issues) => {
                        for issue in file_issues {
                            match issue.severity.as_str() {
                                "Critical" => review.critical_issues += 1,
                                "High" => review.high_issues += 1,
                                "Medium" => review.medium_issues += 1,
                                "Low" => review.low_issues += 1,
                                _ => {}
                            }
                            review.issues.push(issue);
                            review.issues_count += 1;
                        }
                    }
                    Err(e) => {
                        eprintln!("âš ï¸  Failed to analyze {file_path}: {e}");
                    }
                }
            }
        }
    }

    // Close progress channel
    drop(progress_tx);

    println!(
        "âœ… AI analysis complete! Found {} issues.",
        review.issues_count
    );
    Ok(review)
}

pub fn perform_analysis(args: &Args) -> Result<Review> {
    // Create a simple runtime for synchronous callers
    let rt = tokio::runtime::Runtime::new()?;
    rt.block_on(perform_analysis_with_progress(args, None))
}

fn should_analyze_file(file_path: &str, args: &Args) -> bool {
    // Check include patterns
    if !args.include_patterns.is_empty() {
        let matches_include = args
            .include_patterns
            .iter()
            .any(|pattern| file_matches_pattern(file_path, pattern));
        if !matches_include {
            return false;
        }
    }

    // Check exclude patterns
    for pattern in &args.exclude_patterns {
        if file_matches_pattern(file_path, pattern) {
            return false;
        }
    }

    // Default exclusions
    if file_path.starts_with("target/")
        || file_path.contains("node_modules/")
        || file_path.ends_with(".lock")
        || file_path.ends_with(".log")
    {
        return false;
    }

    true
}

fn file_matches_pattern(file_path: &str, pattern: &str) -> bool {
    // Simple pattern matching - can be enhanced with glob
    if pattern.starts_with("*.") {
        let extension = &pattern[1..];
        file_path.ends_with(extension)
    } else if let Some(prefix) = pattern.strip_suffix("/**") {
        file_path.starts_with(prefix)
    } else {
        file_path.contains(pattern)
    }
}

fn detect_language(file_path: &str) -> String {
    use std::path::Path;
    let path = Path::new(file_path);
    match path.extension().and_then(|ext| ext.to_str()) {
        Some("rs") => "rust".to_string(),
        Some("js") => "javascript".to_string(),
        Some("ts") => "typescript".to_string(),
        Some("py") => "python".to_string(),
        Some("java") => "java".to_string(),
        Some("cpp") | Some("cc") | Some("cxx") => "cpp".to_string(),
        Some("c") => "c".to_string(),
        Some("go") => "go".to_string(),
        Some("php") => "php".to_string(),
        Some("rb") => "ruby".to_string(),
        Some("cs") => "csharp".to_string(),
        _ => "unknown".to_string(),
    }
}

use anyhow::{anyhow, Result};
use git2::{Repository, Status};

pub struct GitAnalyzer {
    repo: Repository,
}

impl GitAnalyzer {
    pub fn new(repo_path: &str) -> Result<Self> {
        let repo =
            Repository::open(repo_path).map_err(|e| anyhow!("Failed to open repository: {}", e))?;

        Ok(Self { repo })
    }

    pub fn get_changed_files(
        &self,
        source_branch: &str,
        target_branch: &str,
    ) -> Result<Vec<String>> {
        let mut all_files = Vec::new();
        let mut committed_files = std::collections::HashSet::new();

        // Get committed changes between branches
        if source_branch != target_branch {
            let source_commit = self.get_commit(source_branch)?;
            let target_commit = self.get_commit(target_branch)?;

            let source_tree = source_commit.tree()?;
            let target_tree = target_commit.tree()?;

            let diff = self
                .repo
                .diff_tree_to_tree(Some(&source_tree), Some(&target_tree), None)?;

            diff.foreach(
                &mut |delta, _progress| {
                    if let Some(file) = delta.new_file().path() {
                        let file_path = file.to_string_lossy().to_string();
                        committed_files.insert(file_path.clone());
                        all_files.push(file_path);
                    }
                    true
                },
                None,
                None,
                None,
            )?;
        }

        // Get uncommitted changes (staged and modified)
        let uncommitted_files = self.get_uncommitted_files()?;
        for file in uncommitted_files {
            if !committed_files.contains(&file) {
                all_files.push(file);
            }
        }

        Ok(all_files)
    }

    pub fn get_uncommitted_files(&self) -> Result<Vec<String>> {
        let mut files = Vec::new();
        let statuses = self.repo.statuses(None)?;

        for entry in statuses.iter() {
            if let Some(path) = entry.path() {
                files.push(path.to_string());
            }
        }

        Ok(files)
    }

    pub fn get_file_status(&self, file_path: &str) -> Result<super::review::CommitStatus> {
        let statuses = self.repo.statuses(None)?;

        for entry in statuses.iter() {
            if let Some(path) = entry.path() {
                if path == file_path {
                    let status = entry.status();

                    if status.contains(Status::INDEX_NEW)
                        || status.contains(Status::INDEX_MODIFIED)
                        || status.contains(Status::INDEX_DELETED)
                    {
                        return Ok(super::review::CommitStatus::Staged);
                    }

                    if status.contains(Status::WT_NEW) {
                        return Ok(super::review::CommitStatus::Untracked);
                    }

                    if status.contains(Status::WT_MODIFIED) || status.contains(Status::WT_DELETED) {
                        return Ok(super::review::CommitStatus::Modified);
                    }
                }
            }
        }

        // If not in status, assume it's committed
        Ok(super::review::CommitStatus::Committed)
    }

    pub fn get_file_content(&self, file_path: &str, branch: &str) -> Result<String> {
        // First check if file has uncommitted changes
        let file_status = self.get_file_status(file_path)?;

        match file_status {
            super::review::CommitStatus::Untracked | super::review::CommitStatus::Modified => {
                // Read from working directory
                let full_path = self
                    .repo
                    .workdir()
                    .ok_or_else(|| anyhow!("Repository has no working directory"))?
                    .join(file_path);

                std::fs::read_to_string(&full_path)
                    .map_err(|e| anyhow!("Failed to read file from working directory: {}", e))
            }
            super::review::CommitStatus::Staged => {
                // Try to read from index first, fall back to working directory
                match self.get_file_content_from_index(file_path) {
                    Ok(content) => Ok(content),
                    Err(_) => {
                        let full_path = self
                            .repo
                            .workdir()
                            .ok_or_else(|| anyhow!("Repository has no working directory"))?
                            .join(file_path);

                        std::fs::read_to_string(&full_path).map_err(|e| {
                            anyhow!("Failed to read file from working directory: {}", e)
                        })
                    }
                }
            }
            super::review::CommitStatus::Committed => {
                // Read from commit
                let commit = self.get_commit(branch)?;
                let tree = commit.tree()?;

                let entry = tree.get_path(std::path::Path::new(file_path))?;
                let object = self
                    .repo
                    .find_object(entry.id(), Some(git2::ObjectType::Blob))?;
                let blob = object
                    .as_blob()
                    .ok_or_else(|| anyhow!("Object is not a blob"))?;

                let content = std::str::from_utf8(blob.content())
                    .map_err(|e| anyhow!("Invalid UTF-8 in file: {}", e))?;

                Ok(content.to_string())
            }
        }
    }

    fn get_file_content_from_index(&self, file_path: &str) -> Result<String> {
        let index = self.repo.index()?;
        let entry = index
            .get_path(std::path::Path::new(file_path), 0)
            .ok_or_else(|| anyhow!("File not found in index"))?;

        let object = self
            .repo
            .find_object(entry.id, Some(git2::ObjectType::Blob))?;
        let blob = object
            .as_blob()
            .ok_or_else(|| anyhow!("Object is not a blob"))?;

        let content = std::str::from_utf8(blob.content())
            .map_err(|e| anyhow!("Invalid UTF-8 in file: {}", e))?;

        Ok(content.to_string())
    }

    fn get_commit(&self, branch_name: &str) -> Result<git2::Commit<'_>> {
        let reference = if branch_name == "HEAD" {
            self.repo.head()?
        } else {
            self.repo
                .find_reference(&format!("refs/heads/{branch_name}"))?
        };

        let oid = reference
            .target()
            .ok_or_else(|| anyhow!("Invalid reference"))?;

        self.repo
            .find_commit(oid)
            .map_err(|e| anyhow!("Failed to find commit: {}", e))
    }
}

